import React, {Component} from "react";

class TodoList extends Component{

	constructor(){
		super();

		this.state = {
			"items":[]
		}
		this.completeItem = this.completeItem.bind(this);
	}

	componentDidMount(){
	fetch('/todolist', {
		  method: 'get',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  }
		}).then((res) => res.json()).then((res) => this.setState({"items":res.items})).catch(err=>console.log(err));

	}

	completeItem(){
		fetch('/complete_item', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({username:'charlie'})
		}).then((res) => {console.log(res.status); this.props.history.push('/todolist');}).catch(err=>console.log(err));


	}

	render(){
		let rendered_items = [];
		if(this.state.items.length > 0){
			let t = this;
		this.state.items.forEach(function(item){
			let temp = (<div></div>);
			if(item.completed){
				temp = (<div className = "todolistitem">
        			<p>{item.content}</p>
        		</div>);
			}else{
				temp = (<div className = "todolistitem">
        			<p>{item.content}</p>
        			<button onClick = {()=>{t.completeItem(item.user_id, item.id)}}>complete</button>
        		</div>);
			}





			rendered_items.push(temp);
		})
		}


		return (<div>
			<div className = "todolistview">
        		{rendered_items}
        		<div className = "todolistitem">
        			<input/>
        			<button  onClick = {this.completeItem}>add</button>
        			</div>
        	</div>
		</div>

		);
	}
}

export default TodoList;
