import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';



class App extends Component {

	constructor(){
		super();
		this.login = this.login.bind(this);
	}

	login(){
		/*

						 request.post('https://hunter-todo-api.herokuapp.com/auth', {json:{"username":username}}, function(error, response, body) {
						 res.cookie('sillyauth', body.token);
					     res.redirect('/get_items_action');
					    });
		*/

		fetch("https://hunter-todo-api.herokuapp.com/auth",{mode:"cors",method:"post",body:{"username":"charlie"}}).then(res=>console.log(res)).catch(err=>console.log(err));
	}
	signup(){
		console.log("logging in..");
	}

	github_serve(){
		console.log("logging in..");
	}


  render() {
    return (
      <div className="App">
      	<button>Github static serve</button>
        <div className = "login_sys">
        	<p>Sign up</p>
        	<input placeholder = "username"/>
        	<button onClick = {this.login}>Register</button>
        </div>
       	<div className = "login_sys">
        	<p>Log in</p>
        	<input placeholder = "username"/>
        	<button onClick = {this.login}>login</button>
        </div>
        <div className = "todolistview">
        	<div className = "todolistitem">
        		<input/>
        		<button>add</button>
        	</div>
        </div>
        <button id = "logout" onClick = {this.login}>logout</button>
      </div>
    );
  }
}

export default App;
