import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';



class App extends Component {

	constructor(){
		super();
		this.login = this.login.bind(this);
		this.state = {
			"secret_token":""
		}
	}

	login(){
		fetch('https://hunter-todo-api.herokuapp.com/auth', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({username:'charlie'})
		}).then(res => res.json()).then(res=>(this.setState({"secret_token":res}))).catch(err=>console.log(err));

	}
	signup(){
		console.log("logging in..");
	}

	github_serve(){
		console.log("logging in..");
	}


  render() {
    return (
      <div className="App">
      	<button>Github static serve</button>
        <div className = "login_sys">
        	<p>Sign up</p>
        	<input placeholder = "username"/>
        	<button onClick = {this.login}>Register</button>
        </div>
       	<div className = "login_sys">
        	<p>Log in</p>
        	<input placeholder = "username"/>
        	<button onClick = {this.login}>login</button>
        </div>
        <div className = "todolistview">
        	<div className = "todolistitem">
        		<input/>
        		<button>add</button>
        	</div>
        </div>
        <button id = "logout" onClick = {this.login}>logout</button>
      </div>
    );
  }
}

export default App;
