import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import TodoList from "./TodoList";
import {BrowserRouter as Router} from 'react-router-dom';
import {Route, Link} from 'react-router-dom';
import $ from 'jquery'; 


/*
 <TodoList token = {this.state.token}/>
*/


class App extends Component {

	constructor(){
		super();
		this.login = this.login.bind(this);
		this.state = {
			"token":""
		}
	}

	login(){
		$.ajax({
	      url: "https://hunter-todo-api.herokuapp.com/auth",
	      dataType: 'json',
	      cache: false,
	      success: function(data) {
	        console.log(data)
	      },
	      error: function(xhr, status, err) {
	        console.log(err);
	      }
	    });

	}
	signup(){
		console.log("logging in..");
	}

	github_serve(){
		console.log("logging in..");
	}


  render() {
    return (
      <div className="App">
      	<button>Github static serve</button>
        <div className = "login_sys">
        	<p>Sign up</p>
        	<input placeholder = "username"/>
        	<button onClick = {this.login}>Register</button>
        </div>
       	<div className = "login_sys">
        	<p>Log in</p>
        	<input placeholder = "username"/>
        	<button onClick = {this.login}>login</button>
        </div>


        <Router>
        	<Link to = "/todolist">todoooo</Link>
       		 <Route path = "/" exact render = {()=>{return(<div></div>)}}/>
       		 <Route path = "/todolist" exact strict render = {()=>{return(<TodoList token = {this.state.token}/>)}}/> 
        </Router>



        <button id = "logout" onClick = {this.login}>logout</button>
      </div>
    );
  }
}

export default App;
