import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import TodoList from "./TodoList";
import GithubServe from "./GithubServe";
import {BrowserRouter as Router} from 'react-router-dom';
import {Route, Link} from 'react-router-dom';
import {
  withRouter
} from 'react-router-dom';


/*
 <TodoList token = {this.state.token}/>
*/


class App extends Component {

	constructor(){
		super();
		this.login = this.login.bind(this);
		this.signup = this.signup.bind(this);
		this.state = {
			"token":"",
		}
	}

	login(){
		fetch('/login_action', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({username:'charlie'})
		}).then(res => console.log(res)).catch(err=>console.log(err));

	}
	signup(){
		fetch('/create_user_action', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({username:'charlie'})
		}).then(res => console.log(res)).catch(err=>console.log(err));
	}



  render() {
    return (
      <div className="App">




        <Router>


        	 <Route path = "/github_serve" exact component = {GithubServe}/>
       		 <Route path = "/" exact render = {()=>{return(<div>
       		 				<Link to = "/todolist" style = {{all:"unset"}}>todoooo</Link>
       		 				<Link to = "/github_serve" style = {{all:"unset"}}>Github static serve</Link>
							<div className = "login_sys">
							<p>Sign up</p>
							<input placeholder = "username"/>
							<button onClick = {this.signup}>Register</button>
							</div>
							<div className = "login_sys">
							<p>Log in</p>
							<input placeholder = "username"/>
							<button onClick = {this.login}>login</button>
							</div>
							<button id = "logout" onClick = {this.login}>logout</button>

			</div>)}

       		}/>
       		 <Route path = "/todolist" exact strict render = {()=>{return(<TodoList token = {this.state.token}/>)}}/> 
        </Router>




      </div>
    );
  }
}

export default App;
