import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import TodoList from "./TodoList";
import {BrowserRouter as Router} from 'react-router-dom';
import {Route, Link} from 'react-router-dom';
import {
  withRouter
} from 'react-router-dom';


/*
 <TodoList token = {this.state.token}/>
*/


class App extends Component {

	constructor(){
		super();
		this.login = this.login.bind(this);
		this.signup = this.signup.bind(this);
		this.github_serve = this.github_serve.bind(this);
		this.state = {
			"token":"",
			"github_page":""
		}
	}

	login(){
		fetch('/login_action', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({username:'charlie'})
		}).then(res => console.log(res)).catch(err=>console.log(err));

	}
	signup(){
		fetch('/create_user_action', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({username:'charlie'})
		}).then(res => console.log(res)).catch(err=>console.log(err));
	}

	github_serve(){
		fetch('/github', {
		  method: 'get',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  }
		}).then(res => res.text()).then((res)=>{

			this.setState({"github_page":res});
			this.props.history.push('/todolist');


		}).catch(err=>console.log(err));
	}


  render() {
    return (
      <div className="App">
      	<button onClick={this.github_serve}>Github static serve</button>
        <div className = "login_sys">
        	<p>Sign up</p>
        	<input placeholder = "username"/>
        	<button onClick = {this.signup}>Register</button>
        </div>
       	<div className = "login_sys">
        	<p>Log in</p>
        	<input placeholder = "username"/>
        	<button onClick = {this.login}>login</button>
        </div>


        <Router>
        	<Link to = "/todolist">todoooo</Link>
       		 <Route path = "/" exact render = {()=>{return(<div></div>)}}/>
       		 <Route path = "/todolist" exact strict render = {()=>{return(<TodoList token = {this.state.token}/>)}}/> 
        </Router>



        <button id = "logout" onClick = {this.login}>logout</button>
      </div>
    );
  }
}

export default App;
