import React, {Component} from "react";

class TodoList extends Component{

	constructor(){
		super();
		this.completeItem = this.completeItem.bind(this);
		this.state = {
			"items":[]
		}
	}
	componentDidMount(){
		 /*request.get('https://hunter-todo-api.herokuapp.com/todo-item', {headers: {'Cookie': s}}, function(error, response, body) {
	 	res.render('items',{items:JSON.parse(body)});
    	});*/
    	fetch('/todolist/', {
		  method: 'get',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  }
		}).then(res => res.json()).then(res=>this.setState({"items":res})).catch(err=>console.log(err));

	}
	completeItem(user_id, item_id){
		fetch('/ob', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body:{
		  	"item_id":item_id
		  }
		}).then(res=>console.log(res.status)).catch(err=>console.log(err));

	}

	render(){
		let rendered_items = [];
		let t = this;
		this.state.items.forEach(function(item){
			let temp = (<div></div>);
			if(item.completed){
				temp = (<div className = "todolistitem">
        			<p>{item.content}</p>
        		</div>);
			}else{
				temp = (<div className = "todolistitem">
        			<p>{item.content}</p>
        			<button onClick = {()=>{t.completeItem(item.user_id, item.id)}}>complete</button>
        		</div>);
			}





			rendered_items.push(temp);
		})
		return (<div>
			<div className = "todolistview">
        		{rendered_items}
        		<div className = "todolistitem">
        			<input/>
        			<button  onClick = {()=>{t.completeItem("e", "e")}}>add</button>
        			</div>
        	</div>
		</div>

		);
	}
}

export default TodoList;
