import React, {Component} from "react";
import {Route, Link} from 'react-router-dom';

import TodoList from "./TodoList";
class Home extends Component{
	constructor(){
		super();
		this.login = this.login.bind(this);
		this.signup = this.signup.bind(this);
		this.state = {
			"token":"",
		}
	}

	login(){
		fetch('/login_action', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({username:'charlie'})
		}).then(res => res.json()).then(res => this.setState({"token":res.token})).catch(err=>console.log(err));

	}
	signup(){
		fetch('/create_user_action', {
		  method: 'post',
		  headers: {
		    'Accept': 'application/json, text/plain, */*',
		    'Content-Type': 'application/json'
		  },
		  body: JSON.stringify({username:'charlie'})
		}).then(res => console.log(res)).catch(err=>console.log(err));
	}


render(){
	return(

		<div>
       		 				<Link to = "/todolist" style = {{all:"unset"}}>todoooo</Link><br/>
       		 				<Link to = "/github_serve" style = {{all:"unset"}}>Github static serve</Link>
							<div className = "login_sys">
							<p>Sign up</p>
							<input placeholder = "username"/>
							<button onClick = {this.signup}>Register</button>
							</div>
							<div className = "login_sys">
							<p>Log in</p>
							<input placeholder = "username"/>
							<button onClick = {this.login}>login</button>
							</div>
							<button id = "logout" onClick = {this.login}>logout</button>
							<Route path = "/todolist" exact strict render = {()=>{return(<TodoList token = {this.state.token}/>)}}/> 


			</div>

		);
}

}

export default Home;
